import { ref } from 'vue';
export default {
  setup(props, context) {
    const _refInput = ref(null);
    const _title = ref('');
    const fnAddListHandler = () => {
      const data = {
        id: Date.now(),
        title: _title.value,
        completed: false
      };
      context.emit('emitAddListHandler', data);
      _title.value = '';
      _refInput.value.focus();
    };
    return {
      _title,
      fnAddListHandler,
      _refInput
    };
  }
};
