  const [_outputArr, _setOutputArr] = useState(fnSetArr()) //이게 화면 출력용

  console.log(fnSetArr())

  return (
    <>
      <h1>todoApp</h1>
      <hr />
      <Routes>
        <Route path="/" element={<CompMian/>}/>
