import React, { useState } from 'react';

const fnGetRandom =() => Math.floor(Math.random()*10)

const CompLi = () => {

 const [_num1, _setNum1] = useState(fnGetRandom())
 const [_num2, _setNum2] = useState(fnGetRandom())

function fnOnInputHandler() {
  (_num1+_num2===parseInt(e.target.value))? 'o':'x' //이 값을 해당배열의 해당번째 애가 받는것
}


  return (
    <li>
      <span>{_num1}</span>
      +
      <span>{_num2}</span>
      =
      <input onInput={fnOnInputHandler} type="number" min="0" max="9"/>
    </li>
  );
};

export default CompLi;
