const CompModal = () => {
  useEffect(()=>{
let modal = document.querySelector('modal')
modal.classList.add(`active`)
  },[])//태어났을때 딱 한번(새로고침)
  return (
    <div className='modal'>
      <p>게임끝!</p>
      <button>초기화</button>

    </div>
  );
};

export default CompModal;
