import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { AppContext } from '../App';

const CompDetail = () => {
  const { _todoArr } = useContext(AppContext)
  const params = useParams() //링크걸때 아이디를 실어서 보냈는데, 주소에 보냈어도 받아먹어야하잖아. 주소뒤에찍어보내는게 파라미터. 확인하려면 리액트에서는 유즈파람을 이용해서 객체로 그 정보를 받아올수있다.
  const { id } = params

  const obj = _todoArr.find(v => v.id === parseInt(id))
  const { title, date, time, desc } = obj


  return (
    <div>
      <h2>세부일정내용</h2>
      {/* <p>{title}</p>
      <p>{date}</p>
      <p>{time}</p>
      <p>{desc}</p> */}
      <input type="text" defaultValue={title}/> {/* 여기서 디폴트벨류로 바꿔버리면 스테잇값을 받았을때 수정이 ㄷ안됨. 지금은 아니니까사용가능 */}
      <input type="date" defaultValue={date}/>
      <input type="time" defaultValue={time}/>
      <textarea  defaultValue={desc}></textarea>
    </div>
  );
};

export default CompDetail;
