import { useState } from "react";

const fnGetArr = () => {
  const arrStrg = localStorage.getItem('arrStorage')
  if(arrStrg) {
    return JSON.parse(arrStrg)
  }else {
    localStorage.setItem( 'arrStorage',JSON.stringify([1]) )
    return [1]
  }
}//fn
// 로컬스토리지에 값이 있으면 가져오고 없으면 1의 값을 리턴하는 함수를 만든다
// 스테이트를 직접작성하는게 아니면 밖에서 만들어야한다

function App() {
  const [_add, _setAdd] = useState(1)
  const [_arr,_setArr] = useState(fnGetArr)

  const fnAdd = function(){
    const i = _arr[_arr.length-1]+1
    const arr = [..._arr,i]
    window.localStorage.setItem('arrStorage',JSON.stringify(arr))
    _setArr(arr)
    _setAdd(arr)
  }

  const fnIncreaseHandler = () => {
    _setAdd(prev => prev + 1)
  }

  return (
    <>
      {_add}
      <hr />
      <button onClick={fnIncreaseHandler}>증가</button>
    </>
  );
}

export default App;


