import CompSum from "./components/CompSum";


function App() {

let arrComp = ['','','','',''] 

  return (
    <>
      <h1>더하기연습</h1>
      {
        arrComp.map((v)=>{
          return <CompSum key={v}/>//배열을 리턴할때는 키를 사용한다. 최적화를 위해서 //근데 i를 주면 내가 수정을 해버리면 그게 적용이 안되고 계속해서 0부터 나오게 됨. 즉 키값이 바뀔때마다 다시 그리게 됨 //하지만 v를 넣으면 v번째잖아 그러니까 딱 걔만 지울 수가 있는거.
        })
      }
    </>
  );
}

export default App;
