const CompOutput = () => {

  const [_num, _setNum] = useState(0)

  useEffect(() => {   
    document.querySelector('button').addEventListener(`click`, e => {
    })
    setInterval(function () {  
      _setNum(prev => prev + 1)
    }, 1000)

  }, [])
  return (
    < >
      <p>1씩 증가 :{_num}</p>
      <button>중지</button>
    </ >
  );
};

export default CompOutput;
