import { createContext, useState } from "react";
import CompResult from "./components/CompResult";
import CompUl from "./components/CompUl";

const AppContext = createContext() //전달해주고싶으면 크리에잇 컨텍스트. 익스폴트를 만드시 해줘야함. 그래서 앱 안에서 만들면 안됨. app이 익스폴트가 이미 되어있어서 밖에서 해야함

function App() {
  const [_pointArr, _setPointArr] = useState(new Array(3).fill('x'))//점수출력용


  return (
    <AppContext.Provider value={{
      _pointArr, _setPointArr
    }}> {/* 전달할 수 있는 범위가 프로바이더, value는 보낼 객체..??그래서 중괄호를 하나 더 열어서 다 담아서 보낸다 */}
      <h1>context</h1>
      <hr />
      <CompUl />
      <hr />
      <CompResult />
    </AppContext.Provider>
  );
}

export default App;
