import React, { useEffect, useState } from 'react';

const CompInterval = () => {

  const [_num, _setNum] = useState(0)

  useEffect(() => {
    setInterval(() => {
      _setNum(prev => prev + 1)
    }, 1000);
  }, [])//화면에 나왔을때 단 1회만 할것.

  return (
    <p>
      _num : {_num}</p>
  );
};

export default CompInterval;
