import { createContext, useContext, useEffect, useState } from "react";
import CompResult from "./components/CompResult";
import CompUl from "./components/CompUl";

export const AppContext = createContext()

function App() {
  const [_liCnt, _setLiCnt] = useState(Math.ceil(Math.random() * 3) + 1)  //배열 개수를 생성하는 스테잇을 만들고야 배열의 개수는 랜덤.근데 다음에 화면 업데이트해도 안읽으니까 여기서 랜덤으로 만들면 안됨. 초기화를 안하고 바뀐상태만 기억함. 그래서 셋리카운트를 해줄때까지는 안변하고 아님 새로고침을 해줘야하는것.
  
  
  useEffect(() => {
    console.log('App update');//정답일때만 업데이트가 일어난다 왜냐면 스테이트가 그때만 변경되잖아
  })

  return (
    <AppContext.Provider value={{ //밖은 보간법중괄호, 안은 변수(객체)중괄호
      _liCnt, _setLiCnt
    }}>
      <h1>더하기 문제</h1>
      <CompUl />
      <hr />
      <CompResult />
      <button>초기화</button>
    </AppContext.Provider>
  );
}

export default App;
