import React, { useContext } from 'react';
import { AppContext } from '../App';
import CompLi from './CompLi';

const CompMain = () => {
  const { _todoArr } = useContext(AppContext)
  return (
    <main>
      <h2>일정목록</h2>
      <ul>{/* 투두어레이만큼 li가 배열로 출력이 되는걸 만들거란 소리 */}
        {
          _todoArr.map(v =><CompLi
             key={v.id} title={v.title} />) //컴포넌트를 2개를 만들어서 배열로 리턴하라는 소리 //i는 절대안됨. 리드로잉하는거랑 같아. 그럴거면 키를 안주는거랑 똑같아
        }
      </ul>
    </main>
  );
};

export default CompMain;
