import { createContext, useContext, useState } from "react";
import CompResult from "./CompResult";
import CompUl from "./components/CompUl";

export const AppContext = createContext()

function App() {
  const [_liCnt, _setLiCnt] = useState(Math.ceil(Math.random() * 3) + 1)  //배열 개수를 생성하는 스테잇을 만들고야 배열의 개수는 랜덤.근데 다음에 화면 업데이트해도 안읽으니까 여기서 랜덤으로 만들면 안됨. 초기화를 안하고 바뀐상태만 기억함

  return (
    <AppContext.Provider value={{ //밖은 보간법중괄호, 안은 변수(객체)중괄호
      _liCnt, _setLiCnt
    }}>
      <h1>더하기 문제</h1>
      <CompUl />
      <hr />
      <CompResult />
    </AppContext.Provider>
  );
}

export default App;
