import React, { useContext, useState } from 'react';
import { AppContext } from '../App';

const fnGetRandom = () => Math.floor(Math.random() * 10)


const CompLi = ({ i }) => {
  // const fnChangePoint = function (arr) {
  //   return arr.filter(v => v === 'o').length
  // }
  const { _pointArr, _setPointArr, fnChangePoint } = useContext(AppContext)
  const [_num1, _setNum1] = useState(fnGetRandom())
  const [_num2, _setNum2] = useState(fnGetRandom())
  const [_readOnly, _setReadOnly] = useState(false)

  function fnOnInputHandler(e) {
    const pointArr = [..._pointArr];
    //여기서 1은 배열의 번호를 말하는거자나 그러니까 해당번째를 바꿀려면 해당번째를 넣어야하는데 그게 i 라는거지
    pointArr[i] = (_num1 + _num2 === parseInt(e.target.value)) ? 'o' : 'x' //이 값을 해당배열의 해당번째 애가 받는것
    _setPointArr(pointArr)
    (fnChangePoint(pointArr) ===3)? true : false
  }


  return (
    <li>
      <span>{_num1}</span>
      +
      <span>{_num2}</span>
      =
      <input onInput={fnOnInputHandler} type="number" min="0" max="9" readOnly={_readOnly} />
    </li>
  );
};

export default CompLi;
