import { useEffect, useState } from "react";
import CompPopup from "./components/CompPopup";

window,localStorage.setItem('expires','들어감')


function App() {
  const [_show, _setShow] = useState(true)
  useEffect(() => {
    if (window.localStorage.getItem('expires')) {
      _setShow(false)
    } else {
      _setShow(true)
    }
    //이런게 존재하니?지금은 없엉. 보여줄지 말지 결정할건데. 스토리지에 없으면 보여주고 스토리지에 있으면 안보여주고
  }, [])
  return (
    <>
      <h1>유효기간 팝업</h1>
      {_show && <CompPopup />}
    </>
  );
}

export default App;
