import { useEffect, useState } from "react";


const CompOutput = ({ _num, _setNum }) => {

  const [_n, _setN] = useState(1)


  useEffect(() => {
    document.querySelector(`.Btn`).click(e=>{
      const intervalID = setInterval(function () {
        _setNum(prev => prev + 1)
      }, 1000)
      return(()=>{
        clearInterval(intervalID)
      })
    })
  }, [])


  return (
    < >
      <p >1씩 증가 :{_n}</p>
      <button className="Btn" >중지</button>
    </ >
  );
};

export default CompOutput;
