import { useState } from "react";
import { useEffect } from "react";

function fnGetDDay(dday, now) {
  let dDayTimeStamp = dday
  let timeStamp = now
  let diff = dday - now
  let diffSec = parseInt(diff / 1000)
  let date = diffSec % (24 * 60 * 60) //남은 날짜

//60분이 1시간이니까 3600초, 소수점이 나오니까 floor로 소수점을 내려버림
}
function App() {

  // const day = new Date()
  // day.getTime()
  let timeStamp = Date.now()//1000곱한 시간이 지금까지 흐른 초, 실시간으로 구해야함(업데이트되어야함, 그래서 셋인터벌)
  //내가 만약 미래 특정시간(바뀌지 않는 시간)을 구하고 싶으면 미래시간 - 지금시간을 빼면 됨.
  const dDayTimeStamp = new Date(2023, 4, 26).getTime() //이렇게 만들면 현재 날짜 아니면 안에 날짜를 넣어줘야 디데이가 됨
  const [_hour, _setHour] = useState()
  const [_min, _setMin] = useState()
  const [_sec, _setSec] = useState()

  fnGetDDay(dDayTimeStamp, timeStamp)


  useEffect(() => {
    let intervalID = setInterval(() => {
      timeStamp = Date.now()
      // console.log(timeStamp);
    }, 1000)//항상 습관적으로 클리어 할것
    return (() => {
      clearInterval(intervalID)
    })
  }, [])
  // console.log(timeStamp);
  return (
    <>
      <h1>dDay</h1>
      <p>{_hour}:{_min}:{_sec}</p>
    </>
  );
}

export default App;
