import { Route, Routes } from "react-router-dom";
import CompAdd from "./coponents/CompAdd";
import CompDetail from "./coponents/CompDetail";
import CompMian from "./coponents/CompMian";
import { useState } from "react";

function App() {
  const [_todoArr, _setTodoArr] = useState() //화면에 출력되는 목록이랑 진짜리스트 두개를 만들거야 출력되는 목록을따로 만드는 이유는 원본을 보존하지 않으면 검색기능 등을 활용했을때 다 지워버리면 안되니까 출력용, 원본용(회원추가나 삭제, 변경)
  return (
    <>
      <h1>todoApp</h1>
      <hr />
      <Routes>
        <Route path="/" element={<CompMian/>}/>
        <Route path="/add" element={<CompAdd/>}/>
        <Route path="/detail" element={<CompDetail/>}/>
      </Routes>
    </>
  );
}

export default App;
